import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';

const dadosFicticios = [
  { id: 1, title: 'Blockchain developer best practices on innovationchain', data: '3 weeks', answers: ['Answer 1', 'Answer 2'] },
  { id: 2, title: 'Como estilizar um button?', data: '2 weeks', answers: ['Answer 3', 'Answer 4'] },
];

const QuestionComents = () => {
  const { id } = useParams();
  const [question, setQuestion] = useState(null);

  useEffect(() => {
    const foundQuestion = dadosFicticios.find(q => q.id === parseInt(id));
    setQuestion(foundQuestion);
  }, [id]);

  if (!question) {
    return <div>Loading...</div>;
  }

  return (
    <div className="question-detail">
      <h2>{question.title}</h2>
      <div className="answers">
        {question.answers.map((answer, index) => (
          <div key={index} className="comment">
            <p>{answer}</p>
          </div>
        ))}
        <div className="public-comment">
          <input type="text" placeholder="Adicione um comentÃ¡rio" />
          <button>SEND</button>
        </div>
      </div>
    </div>
  );
};

export default QuestionDetail;
