import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';

/* Todos comentários*/
const respostas = [
    {id:1, idUser: 1, idTopico: 1, resposta: 'acho que não ein', data: 'today'},
    {id:2, idUser: 1, idTopico: 1, resposta: 'calma calabreso', data: 'today'},
    {id:3, idUser: 2, idTopico: 2, resposta: 'Incrivel', data: 'today'}
]

/* Todas as perguntas*/
const topicos = [
    {id: 1, idUser: 1, title: 'Blockchain developer best practices on innovationchain', data: '3 weeks'},
    {id: 2, idUser:2, title: 'Como estilizar um button?', data: '2 weeks'},
    
]

const QuestionComents = () => {

  const { id } = useParams();
  const [comments, setComments] = useState([]);
  const [title, setTitle] = useState('');
  const [newComment, setNewComment] = useState('');
  

  useEffect(() => {

    /* Obter todos os comentários com o mesmo idTopico */
    const foundComments = respostas.filter(q => q.idTopico === parseInt(id));
    setComments(foundComments);

    /* Encontrar o titulo dos comentários exibidos */
    const foundTitle = topicos.find(t => t.id === parseInt(id));
    if(foundTitle){
        setTitle(foundTitle.title)
    }

  }, [id]);

  const AddComment = () => {
    if (newComment) {
        const newCommentText = {
            id: comments.length + 1,
            idUser: 1,  
            idTopico: parseInt(id),
            resposta: newComment,
            data: 'today'
        };
        setComments([...comments, newCommentText]);
    }
    };

  
  if (!comments) {
    return <div>Nenhum comentário!</div>;
  }

  return (
    <div className="question-detail">
        <div className="question-item">
            <h3>{title}</h3>
            <h3 className='titulo-question' onClick={handleQuestionClick}>{topico.title}</h3>
      <div className='row space-between'>
            <div className='row'>
                <div>
                    <img className='icone-usuario' src={imagemUser}></img>
                </div>
                <div>
                    <p className='username'>{user}</p>
                    <p className='data'>{topico.data}</p>
                </div>
            </div>
           
            <div>
                <span className='likes'>{likes} likes </span>
                <span className='comentarios'>{comentarios} comments </span>
            </div>
        
      </div>
        </div>
        <div className="answers">
            {comments.map((answer, index) => (
            <div key={index} className="comment">
                <p>{answer.resposta}</p>
            </div>
            ))}
            <div className="public-comment">
                <input type="text" placeholder="Adicione um comentário" 
                        value={newComment}
                        onChange={(e) => setNewComment(e.target.value)}/>
                <button onClick={AddComment}>SEND</button>
            </div>
      </div>
    </div>
  );
};

export default QuestionComents;
