import React, {useState, useEffect} from "react";
//import axios from 'axios';
import QuestionItem from "./questionItem";

const dadosFicticios = [
    {id: 1, title: 'Blockchain developer best practices on innovationchain', data: '3 weeks', answers: ['cool', 'esquece!']},
    {id: 2, title: 'Como estilizar um button?', data: '2 weeks', answers:['m처 daora', 'N찾o creio']},

]

const QuestionList = () => {
    const [questions, SetQuestions] = useState([]);
    const [selectedQuestionId, setSelectedQuestionId] = useState(null);

    useEffect(() => {
        /*
        const fetchQuestion = async () => {
            try{
                const responde = await axios.get('api)}
                setQuestion(responde.data)
            } catch (error){
                console.log("Error")}
             }

        fetchQuestion();

         */
        SetQuestions(dadosFicticios)
    }, []);

    const handleQuestionClick = (id) => {
        setSelectedQuestionId(id === selectedQuestionId ? null : id);
      };

    return (
        <div className="question-list">
            {questions.map(question => (
                <QuestionItem key={question.id} question={question} isSelected={question.id === selectedQuestionId}
                onQuestionClick={() => handleQuestionClick(question.id)}/>
            ))}
        </div>
    )
};

export default QuestionList;
