import React, {useState, useEffect} from "react";
import QuestionComents from "./questionComents";
import { useParams } from "react-router-dom";

const usuario = [
    {id: 1, nome: 'Iara Amancio', email: 'iara@gmail.com', senha: '1234'},
    {id: 2, nome: 'Iara Costa', email: 'iara@gmail.com', senha: '1234'},
]

const topicos = [
    {id: 1, idUser: 1, title: 'Blockchain developer best practices on innovationchain', data: '3 weeks'},
    {id: 2, idUser:2, title: 'Como estilizar um button?', data: '2 weeks'},
]

const likeTopicos = [
    {id: 1, idUser: 1, idTopico: 1},
    {id: 2, idUser: 2, idTopico: 1},
]

const respostas = [
    {id:1, idUser: 1, idTopico: 1, resposta: 'acho que não ein', data: 'today'},
    {id:2, idUser: 1, idTopico: 1, resposta: 'calma calabreso', data: 'today'}
]

const likeRespostas = [
    {id: 1, idUser: 1, idResposta: 1},
]

const QuestionCommentsList = () => {
    const [answers, SetAnswers] = useState([]);
    const { idTopic } = useParams();

    useEffect(() => {

        /*
        const fetchQuestion = async () => {
            try{
                const responde = await axios.get('api)}
                setQuestion(responde.data)
            } catch (error){
                console.log("Error")}
             }

        fetchQuestion();

         */

        SetAnswers(respostas.filter);
    }, []);

    

    return (
        <div>
            <div className="answers">
                {answers.map(answer => (
                    <QuestionComents key={answer.id} answer={answer}/>
                ))}
            </div>
        </div>
    )
};

export default QuestionCommentsList;
