import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import imagemUser from '../images/icone-usuario.svg'

/* Todos os comentários*/
const respostas = [
    {id:1, idUser: 1, idTopico: 1, resposta: 'acho que não ein', data: 'today'},
    {id:2, idUser: 2, idTopico: 1, resposta: 'calma calabreso', data: 'today'},
    {id:3, idUser: 2, idTopico: 2, resposta: 'Incrivel', data: 'today'}
]

/* Todas as perguntas*/
const topicos = [
    {id: 1, idUser: 1, title: 'Blockchain developer best practices on innovationchain', data: '3 weeks'},
    {id: 2, idUser:2, title: 'Como estilizar um button?', data: '2 weeks'},  
]

/* usuarios */
const usuario = [
  {id: 1, nome: 'Iara Amancio', email: 'iara@gmail.com', senha: '1234'},
  {id: 2, nome: 'Iara Costa', email: 'iara@gmail.com', senha: '1234'},
]

const likeRespostas = [
  {id: 1, idUser: 1, idResposta: 1},
]

const QuestionComents = (answer) => {

  const { id } = useParams();
  const [comments, setComments] = useState([]);
  const [title, setTitle] = useState('');
  const [idUser, setIdUser] = useState('');
  const [newComment, setNewComment] = useState('');
  
  /* Contador da quantidade de likes */
  const [contLikesComment, setContLikesComment] = useState(likeRespostas);

   /* Dar like pelo icone de like na pergunta */
   const [clickLike, setClickLike] = useState(false)

  useEffect(() => {

    const topicoId = parseInt(id);

    /* Obter todos os comentários com o mesmo idTopico */
    const foundComments = respostas.filter(q => q.idTopico === topicoId);
    setComments(foundComments);

    /* atualizar numero de likes */
    setContLikesComment(contLikesComment);

    /* Encontrar o titulo dos comentários exibidos */
    const foundTopic = topicos.find(t => t.id === topicoId);
    if(foundTopic){
        setTitle(foundTopic.title);
        setIdUser(foundTopic.idUser)
    }

    const foundUser = topicos.find(t => t.id === parseInt(id))
    if(foundUser){
      setIdUser(foundUser.idUser)
    }

      // verifica se o usuario ja deu like assim retorna true ou false
      const usuarioLiked = contLikesComment.some(like => like.idUser === idUser && like.idTopico === parseInt(id)); // Supondo idUser 1 como o usuário logado
      setClickLike(usuarioLiked);

  }, [id]);

  const AddComment = () => {
    
    if (newComment) {
        const newCommentText = {
            id: comments.length + 1,
            idUser: 1,  
            idTopico: parseInt(id),
            resposta: newComment,
            data: 'today'
        };
        setComments([...comments, newCommentText]);
        setNewComment('');
    }
    };

    /* Somatório dos likes de uma pergunta */
  const likes = contLikesComment.reduce((soma, like) => {
    return like.idTopico === parseInt(id) ? soma + 1 : soma;
  }, 0);

  const atualizaLikes = () =>
    {   
      if (clickLike)
        // Se o usuário já deu like, removemos o like 
        {
          // retorna uma nova lista de likes sem o like do usuario logado
          let newLikesComments = contLikesComment.filter(like => !(like.idUser === idUser && like.idTopico === parseInt(id)));
          setContLikesComment(newLikesComments);
        } 
        else {
          // se o usuário não deu like, contabilizae cria o novo like
            setContLikesComment(likeRespostas => [...likeRespostas, { id: likeRespostas.length + 1, idUser: idUser,  idResposta: 1, idTopico: id,  }]);
        }
        setClickLike(!clickLike);
    }


  const getUserNameById = (userId) => {
    const user = usuario.find(u => u.id === userId);
    return user? user.nome: 'Usuário desconhecido';
  }

  return (
    <div className="question-detail">

        <div className="question-item column-between">
            <h3 className='titulo-question'>{title}</h3>
            <div className='row space-between'>
                  <div className='row'>
                      <div>
                          <img className='icone-usuario' src={imagemUser}></img>
                      </div>   
                      <div>
                        <p className='username'>{getUserNameById(idUser)}</p>
                      </div>                 
                  </div>         
            </div>
        </div>

        <div className="answers">
            {comments? (comments.map((answer, index) => (
            <div key={index} className="comment column-between">
                <div className='row space-between'>
                   <p >{answer.resposta}</p>
                   <a onClick={atualizaLikes} href='#'>
                      { clickLike ? (<svg width="30" height="32" viewBox="0 0 30 32" fill="none" xmlns="http://www.w3.org/2000/svg">
                              <rect width="30" height="30" rx="15" fill="#2C353D"/>
                              <g filter="url(#filter0_d_3941_181)">
