import React, {useState, useEffect} from 'react';
import { useNavigate } from 'react-router-dom';
import imagemUser from '../images/icone-usuario.svg'


const likeTopicos = [
    {id: 1, idUser: 1, idTopico: 1},
    {id: 2, idUser: 2, idTopico: 1},
    {id: 3, idUser: 1, idTopico: 2},
]

const respostas = [
    {id:1, idUser: 1, idTopico: 1, resposta: 'acho que não ein', data: 'today'},
    {id:2, idUser: 1, idTopico: 1, resposta: 'calma calabreso', data: 'today'},
    {id:3, idUser: 2, idTopico: 2, resposta: 'Incrivel', data: 'today'}
]

const likeRespostas = [
    {id: 1, idUser: 1, idResposta: 1},
]

const usuario = [
    {id: 1, nome: 'Iara Amancio', email: 'iara@gmail.com', senha: '1234'},
    {id: 2, nome: 'Iara Costa', email: 'iara@gmail.com', senha: '1234'},
]


const QuestionItem = ({ topico }) => {
  const navigate = useNavigate();

  const [likesTopic, SetLikesTopic] = useState([]);
  const [user, SetUser] = useState('');

    useEffect(() => {
        SetLikesTopic(likeTopicos);

        /* Encontrar o usuario de cada pergunta */
        const foundUser = usuario.find(u => u.id === topico.idUser);
        if(foundUser){
            SetUser(foundUser.nome)
        }
    }, []);

    /* Direciona para uma pagina contendo só os comentários de cada pergunta */
  const handleQuestionClick = () => {
    navigate(`/questions/${topico.id}`);
  };

    /* Somatório dos likes de uma pergunta */
  const likes = likesTopic.reduce((soma, like) => {
    return like.idTopico === topico.id ? soma + 1 : soma;
  }, 0);

  /* Somatório dos comentários de uma pergunta */
  const comentarios = respostas.reduce((soma, coment)=>{
    return coment.idTopico === topico.id? soma + 1: soma;
  }, 0)

  return (
    <div className="question-item column-between">

      <div>
        <h3 className='titulo-question' onClick={handleQuestionClick}>{topico.title}</h3>
        <svg width="20" height="20" viewBox="0 0 20 20" fill="none" xmlns="http://www.w3.org/2000/svg">
<path d="M19.3441 7.5198L10.8801 0.339514C10.639 0.121018 10.3253 0 9.99991 0C9.67455 0 9.36083 0.121018 9.11976 0.339514L0.656665 7.5199C0.450009 7.70713 0.284848 7.93555 0.171814 8.19047C0.0587794 8.44538 0.000377072 8.72115 0.000366211 9L0.000366211 19.3357C0.000366209 19.5119 0.0703387 19.6808 0.194897 19.8054C0.319456 19.93 0.488403 20 0.664569 20L6.00037 20C6.55265 20 7.00037 19.5523 7.00037 19V15C7.00037 14.436 7.55246 13.9788 8.11666 13.9788H11.8832C12.4475 13.9788 13.0004 14.436 13.0004 15V19C13.0004 19.5523 13.4481 20 14.0004 20H19.3362C19.5123 20 19.6813 19.93 19.8058 19.8054C19.9304 19.6808 20.0004 19.5119 20.0004 19.3357V9C20.0003 8.72114 19.9419 8.44537 19.8289 8.19044C19.7158 7.93551 19.5507 7.70707 19.3441 7.5198Z" fill="white"/>
</svg>

      </div>
      
      <div className='row space-between'>
            <div className='row'>
                <div>
                    <img className='icone-usuario' src={imagemUser}></img>
                </div>
                <div>
                    <p className='username'>{user}</p>
                    <p className='data'>{topico.data}</p>
                </div>
            </div>
           
            <div>
                <span className='likes'>{likes} likes </span>
                <span className='comentarios'>{comentarios} comments </span>
            </div>
        
      </div>
      
      
    </div>
  );
};

export default QuestionItem;
