import React, {useState, useEffect} from 'react';
import { useNavigate } from 'react-router-dom';


const likeTopicos = [
    {id: 1, idUser: 1, idTopico: 1},
    {id: 2, idUser: 2, idTopico: 1},
]

const respostas = [
    {id:1, idUser: 1, idTopico: 1, resposta: 'acho que não ein', data: 'today'},
    {id:1, idUser: 1, idTopico: 1, resposta: 'calma calabreso', data: 'today'}
]

const likeRespostas = [
    {id: 1, idUser: 1, idResposta: 1},
]



const QuestionItem = ({ topico }) => {
  const navigate = useNavigate();

  const [likesTopic, SetLikesTopic] = useState([]);

    useEffect(() => {
    SetLikesTopic(likeTopicos);
    }, []);

  const handleQuestionClick = () => {
    navigate(`/questions/${topico.id}`);
  };
  let likes = 0;
  return (
    <div className="question-item">
      <h3 onClick={handleQuestionClick}>{topico.title}</h3>
      {likesTopic.map(like => (like.idTopico === topico.id ? likes+=1 : 0))
      }
      <span>{likes} likes</span>
    </div>
  );
};

export default QuestionItem;
